mapper.select:
   		SELECT * FROM exhibition
		    JOIN EXHIBITION_DETAIL USING (EXHIBITION_NO)
		WHERE EXHIBITION_NO = #{exhibitionNo}

SELECT COUNT(*)
FROM EXHIBITION_LIKE
WHERE USER_NO = #{userNo} AND EXHIBITION_NO = #{exhibitionNo}

mapper.update:
UPDATE EXHIBITION
SET STATUS = #{status}
WHERE EXHIBITION_NO = #{exhibitionNo}
